import * as api from './api/'


// api.cache.scan
export function scan() {
	const path = 'util/cache?scan=message:*';
	return api.send( { method: 'GET', path} );
}
 
//  api.cache.get(key)
export function get(key) {
	const path = 'util/cache?get='+encodeURI(key);
	return api.send( { method: 'GET', path} );
}
 
//  api.cache.del(key)
export function del(key) {
	const path = 'util/cache?del='+encodeURI(key);
	return api.send( { method: 'GET', path} );
}
 
//  api.cache.clean(key)
export function clean(key) {
	const path = 'util/cache?clean='+encodeURI(key);
	return api.send( { method: 'GET', path} );
}
