import * as api from './api/'


// api.msg.load(mode) 
// mode === 1 util/import
// mode != 1 iosdk/import

export function load(mode) {
    const path = (mode === 1) ? 'util/import' : 'iosdk/import';
    console.log(path);
	return api.get( { path} );
}

 
//  api.msg.save(data)
export function save(data) {
	const path = 'util/store';
	return api.post( { path, data} );
}